mapper.select:
select rownum,boardno, board_writer, board_passwd, board_title, board_date, board_recount, board_content,board_recontent from 
(select boardno, board_writer, board_passwd, board_title, board_date, board_recount, board_content,
(select count(*) from recontent where recontent_boardno=b.boardno) as board_recontent from board b
where board_title like #{keyword} 

where board_writer like #{keyword} 
order by boardno desc)	order by rownum asc

mapper.update:
update board set
		board_title=#{board_title},board_content=#{board_content},
		board_passwd =#{board_passwd } where boardno=#{boardno }

mapper.select:
select count(*) from board
where board_title like #{keyword} 

where board_writer like #{keyword}
